            <input class="a-input a-input-brand" type="text" name="city" id="card-address-city"
                   placeholder="Enter City..." required>
            <input class="a-input a-input-brand" type="text" name="address_1" id="card-address-address-1"
                   placeholder="Enter Address 1..." required>
            <input class="a-input a-input-brand" type="text" name="address_2" id="card-address-address-2"
                   placeholder="Enter Address 2 (optional) ...">
        </div>
    </div>
    <div class="a-form__item">
        <div class="a-form__item-box">
            <label class="a-form__item__label" for="card-holder-name">Card details</label>
            <input class="a-input a-input-brand" id="card-holder-name" type="text" name="cardholder_name"
                   placeholder="Enter cardholder name..." required>
        </div>
    </div>

